import React,{useState} from 'react'

export default function TextForm(props) {

    const handleUpClick= ()=>{
        console.log("Uppercase Was Clicked" + text);
        let newText= text.toUpperCase();
        setText(newText)
    }

    const handleloClick= ()=>{
        console.log("Lowercase Was Clicked" + text);
        let newText= text.toLowerCase();
        setText(newText)
    }

    const speak = () => {
        let msg = new SpeechSynthesisUtterance();
        msg.text = text;
        window.speechSynthesis.speak(msg);
      }
      const handleSPerLineClick =() =>{
        let newText = text.replaceAll('.',"\n");
        setText(newText);
      }
      const handleCapitalize = () => {
        let newText = text.split(" ").map(el => el.charAt(0).toUpperCase() + el.slice(1)).join(" ");
        setText(newText);
   }
   const handleReverse = (event) => {
    let strArr = text.split("");
    strArr = strArr.reverse();
    let newText = strArr.join("");
    setText(newText);
  };

    const handleOnChange = (event)=>{
        console.log("On Change");
        setText(event.target.value);
    }


    const [text, setText] = useState('Jassi Ek Lajpat Nagar Mein Reine Wala Ek Jaamveer Hai Joh Ki Bahut Kam Papaya Jalta Hai Iska Nickname Chimkaandi , Sundi , Lindi , Maiora Hai . Iski Respect Utni Hi Hai Jitney Suraj Se Vitamin-d Nikalna .yeh Jaamveer Aksar Lajpat Nagar Ki Galliyon Mein Milta Hai . Laziness Mein Isne Phd Kar Rakhi Hai . Isko Koi Bhi Kaam Bolo Toh Bhul Jaata Hai . Teachers Ka Laadla Hai . Jab Bhi Mam Iske Taraf Dekhti hain Toh Isko Aage Aana Padta Hai , Aur Peeche Se Iske Friends "behan Ka Loda" , "behan Ka Lund" Chilaa rahe Hote Hain .kuch Aisa Hi Hai Hamara Jaamveer As Jassi.....');

  return (
      <>
    <div className="container">
    <h1>{props.heading}</h1>
    <div className="mb-3">
    <label for="myBox" class="form-label">Example textarea</label>
   <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="8"></textarea>
   </div>
   <button className="btn btn-primary mx-2" onClick={handleUpClick}>Convert Jassi into Uppercase</button>
   <button className="btn btn-primary mx-2" onClick={handleloClick}>Convert Jassi into Lowercase</button>
   <button className="btn btn-primary mx-2" onClick={speak}>Abuse Jassi plz</button>
   <button className="btn btn-primary mx-2" onClick={handleSPerLineClick}>Break Jassi</button>
   <button className="btn btn-primary mx-2" onClick={handleCapitalize}>Capitalize Jassi</button>
   <button className="btn btn-primary mx-2" onClick={handleReverse}>Reverse Jassi</button>

</div>
<div className="container my-3">
    <h1>Your Text Summary</h1>
    <p>{text.split(" ").length} words and {text.length} characters</p>
    <p>{0.008 * text.split(" ").length } Average Reading Time</p>
    <h2>Preview</h2>
    <p>{text}</p>
</div>


     </>
  )
}
