    <div className="mb-3">
    <label for="myBox" class="form-label">Example textarea</label>
   <textarea className="form-control" id="myBox" rows="8"></textarea>
   </div>
   <button className="btn btn-primary">Convert to uppercase</button>
</div>
  )
}
